import http from "../http-common";

export default {
  name: "",
  data() {
    return {
      bookfineId: {
        memberId: "",
        employeeId: "",
        fineId: "",
        borrowId: ""
      },
      valid: false,
      memberCheck: false,
      memberName: ""
    };
  },
  methods: {
    /* eslint-disable no-console */

    // ดึงข้อมูล Employee ใส่ combobox
    getEmployees() {
      http
        .get("/employee")
        .then(response => {
          this.employees = response.data;
          console.log(response.data);
        })
        .catch(e => {
          console.log(e);
        });
    },
    // ดึงข้อมูล Borrow ใส่ combobox
    getฺนพพนไs() {
      http
        .get("/borrow")
        .then(response => {
          this.borrows = response.data;
          console.log(response.data);
        })
        .catch(e => {
          console.log(e);
        });
    },
    // ดึงข้อมูล FineType ใส่ combobox
    getFineTypes() {
      http
        .get("/fineType")
        .then(response => {
          this.fineTypes = response.data;
          console.log(response.data);
        })
        .catch(e => {
          console.log(e);
        });
    },
    // function ค้นหาลูกค้าด้วย ID
    findMember() {
      http
        .get("/member/" + this.bookfine.memberId)
        .then(response => {
          console.log(response);
          if (response.data != null) {
            this.memberName = response.data.name;
            this.memberCheck = response.status;
          } else {
            this.clear()
          }          
        })
        .catch(e => {
          console.log(e);
        });
      this.submitted = true;
    },
    // function เมื่อกดปุ่ม submit
    saveBookfine() {
      http
        .post(
          "/bookfine/" +
            this.bookfine.memberId +
            "/" +
            this.bookfine.employeeId +
            "/" +
            this.bookfine.borrowId +
            "/" +
            this.bookfine.fineId,
          this.bookfine
        )
        .then(response => {
          console.log(response);
          this.$router.push("/view");
        })
        .catch(e => {
          console.log(e);
        });
      this.submitted = true;
    },
    clear() {
      this.$refs.form.reset();
      this.memberCheck = false;
    },
    refreshList() {
      this.getEmployees();
      this.getBorrows();
      this.getFineTypes();
    }
    /* eslint-enable no-console */
  },
  mounted() {
    this.getEmployees();
    this.getBorrows();
    this.getFineTypes();
  }
};
